// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
    // calculate mean
    var mean = numbers.reduce((a, b) => a + b) / numbers.length;

    // calculate absolute differences from the mean
    var diffs = numbers.map(n => Math.abs(n - mean));

    // calculate mean of the absolute differences
    var mad = diffs.reduce((a, b) => a + b) / diffs.length;

    return mad;
}

