<?php
// You are given a string s.
// if s[i] is a letter, reverse its case from lower to upper or vise versa, 
// otherwise keep it as it is.
// If the string contains no letters, reverse the string.
// The function should return the resulted string.
// Examples
// >>> solve("1234")
// "4321"
// >>> solve("ab")
// "AB"
// >>> solve("#a@C")
// "#A@c"
function solve($s) {
    $result = "";
    $letters = range('a', 'z');
    $upper_case_letters = range('A', 'Z');

    for ($i = 0; $i < strlen($s); $i++) {
        if (in_array($s[$i], $letters)) {
            $result .= ctype_upper($s[$i]) ? strtolower($s[$i]) : strtoupper($s[$i]);
        } else if (in_array($s[$i], $upper_case_letters)) {
            $result .= ctype_lower($s[$i]) ? strtolower($s[$i]) : strtoupper($s[$i]);
        } else {
            $result .= $s[$i];
        }
    }

    if ($result == "") {
        $result = strrev($s);
    }

    return $result;
}

