// Given a positive integer, obtain its roman numeral equivalent as a string,
// and return it in lowercase.
// Restrictions: 1 <= num <= 1000
// Examples:
// >>> int_to_mini_roman(19)
// "xix"
// >>> int_to_mini_roman(152)
// "clii"
// >>> int_to_mini_roman(426)
// "cdxxvi"
function int_to_mini_roman($number) {
    $map = array(
        'i' => 1,
        'v' => 5,
        'x' => 10,
        'l' => 50,
        'c' => 100,
        'd' => 500,
        'm' => 1000
    );

    $roman = '';

    while ($number > 0) {
        foreach ($map as $key => $value) {
            if ($number >= $value) {
                $number -= $value;
                $roman .= $key;
                break;
            }
        }
    }

    // Replace 'i' four times with 'iiii' to get the mini roman numeral
    $roman = str_replace('iiiii', 'v', $roman);
    $roman = str_replace('iiii', 'iv', $roman);
    $roman = str_replace('viv', 'ix', $roman);
    $roman = str_replace('ixx', 'xc', $roman);
    $roman = str_replace('xl', 'xc', $roman);
    $roman = str_replace('lx', 'xc', $roman);
    $roman = str_replace('cccc', 'd', $roman);
    $roman = str_replace('cc', 'cd', $roman);
    $roman = str_replace('dx', 'xc', $roman);
    $roman = str_replace('xxx', 'x', $roman);
    $roman = str_replace('iii', 'i', $roman);

    return $roman;
}

