// true
// >>> iscube(180)
// false
function iscube(a: number): boolean {
    let low = 0;
    let high = Math.abs(a);

    while (low <= high) {
        let mid = Math.floor((low + high) / 2);
        let cube = mid * mid * mid;

        if (cube == Math.abs(a)) {
            return true;
        }
        else if (cube < Math.abs(a)) {
            low = mid + 1;
        }
        else {
            high = mid - 1;
        }
    }

    return false;
}

