function order_by_points(nums: number[]): number[] {
  return nums.sort((a, b) => {
    const sumA = Math.abs(a).toString().split('').reduce((sum, digit) => sum + parseInt(digit), 0);
    const sumB = Math.abs(b).toString().split('').reduce((sum, digit) => sum + parseInt(digit), 0);

    if (sumA === sumB) {
