function largest_prime_factor(n: number): number {
  let largestPrime = -1;

  while (n % 2 === 0) {
    largestPrime = 2;
    n >>= 1;
  }

  for (let i = 3; i * i <= n; i += 2) {
    while (n % i === 0) {
      largestPrime = i;
      n /= i;
