// positive integers that start or end with 1.
function starts_one_ends(n: number): number {
    if (n === 1) return 2; // Special case where n = 1

    let dp = Array(n+1).fill(0).map(() => Array(2).fill(0));
    dp[1][0] = dp[1][1] = 1; // Base case

    for (let i = 2; i <= n; i++) {
        dp[i][0] = dp[i-1][0] + dp[i-1][1]; // The number can end with 0 or 1
