  // Sort coordinates by rows in ascending order.
  coordinates.sort((a, b) => a[0] - b[0]);

  // Sort coordinates of the row by columns in descending order.
  for (let i = 0; i < coordinates.length; i++) {
    coordinates[i] = [coordinates[i][0], lst[coordinates[i][0]].length - coordinates[i][1] - 1];
  }

  return coordinates;
}

