// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words: string[]): string {
    let maxCount = 0;
    let maxWord = '';

    for (let word of words) {
        const uniqueChars = new Set(word.split('')).size;
        if (uniqueChars > maxCount) {
            maxCount = uniqueChars;
            maxWord = word;
        } else if (uniqueChars === maxCount) {
            maxWord = maxWord < word ? maxWord : word;
        }
    }

    return maxWord;
}

